04633 
04647 bool XSTRING::Split(XCHAR separator, XVECTOR<XSTRING*>& results, bool addsubstringempty)
04648 {
04649   if(IsEmpty()) return false;
04650 
04651   int start = 0;
04652 
04653   for(XDWORD c=0; c<sizemem; c++)
04654     {
04655       if((text[c] == separator) || (!text[c]))
04656         {
04657           XSTRING* string = new XSTRING();
04658           if(string)
04659             {
04660               Copy(start, (*string));
04661 
04662               for(XDWORD d=0; d<string->GetSize(); d++)
04663                 {
04664                   if((string->Get()[d] == separator) || (!string->Get()[d]))
04665                     {
04666                       string->Get()[d] = 0;
04667                       break;
04668                     }
04669                 }
04670 
04671               string->AdjustSize();
04672 
04673               if(addsubstringempty)
04674                 {
04675                   if(c)
04676                         results.Add(string);
04677                    else delete string;
04678                 }
04679                else
04680                 {
04681                   if(string->GetSize())
04682                         results.Add(string);
04683                    else delete string;
04684                 }
04685 
04686               start = c+1;
04687 
04688               if((XDWORD)start >= size) break;
04689             }
04690         }
04691     }
04692 
04693   return true;
04694 }
