04696 
04710 bool XSTRING::FormatArg(const XCHAR* mask, va_list* arg, bool isspecialweb)
04711 {
04712   XSTRING str;
04713   bool    openlabel = false;
04714 
04715   Empty();
04716 
04717   if(!mask) return false;
04718 
04719   int c  = 0;
04720 
04721   XCHAR* param = new XCHAR[16];
04722   if(param)
04723     {
04724       while(mask[c])
04725         {
04726           switch(mask[c])
04727             {
04728               case __C('%')   : { int  nparam = 1;
04729                                   bool end    = false;
04730 
04731                                   memset(param,0, 16*sizeof(XCHAR));
04732                                   param[0] = __C('%');
04733 
04734                                   c++;
04735 
04736                                   do{ param[nparam] = mask[c];
04737 
04738                                       nparam++;
04739 
04740                                       switch(mask[c])
04741                                         {
04742                                           case __C('c')   :
04743                                           case __C('C')   : { XCHAR character;
04744 
04745                                                               #if defined(LINUX) || defined(ANDROID) || defined(MICROCONTROLLER)
04746                                                               character = (XCHAR)va_arg((*arg), XDWORD);
04747                                                               #else
04748                                                               character = (XCHAR)va_arg((*arg), int /*XCHAR*/);
04749                                                               #endif
04750 
04751                                                               str.Set(character);
04752                                                               end  = true ;
04753                                                             }
04754                                                             break;
04755    
04756                                           case __C('i')   :
04757                                           case __C('o')   :                                         
04758                                           case __C('x')   :
04759                                           case __C('X')   : { bool large = false;
04760                                                               for(int l=0; l<(int)XSTRING::GetSize(param); l++)
04761                                                                 {
04762                                                                   if(param[l] == __C('l')) large = true;
04763                                                                 }
04764 
04765                                                               if(!large)
04766                                                                 {
04767                                                                   int value = (int)va_arg((*arg), int);
04768 
04769                                                                   str.ConvertFromInt(value, param);
04770                                                                 }
04771                                                                else
04772                                                                 {
04773                                                                   str.ConvertFromLongLong((long long)va_arg((*arg), long long), param);
04774                                                                 }
04775                                                               end  = true;
04776                                                             }                                                           
04777                                                             break;
04778 
04779                                           case __C('d')   :                                       
04780                                           case __C('u')   : { bool large    = false;
04781                                                               bool shortint = false;
04782     
04783                                                               for(int d=0; d<(int)XSTRING::GetSize(param); d++)
04784                                                                 {
04785                                                                   if(param[d] == __C('l')) 
04786                                                                     {
04787                                                                       large = true;
04788                                                                     }
04789 
04790                                                                   if(param[d] == __C('h')) 
04791                                                                     {
04792                                                                       shortint = true;
04793                                                                     }
04794                                                                 }
04795 
04796                                                               if(shortint)
04797                                                                 {
04798                                                                   if(mask[c] == __C('d')) 
04799                                                                     {
04800                                                                       short value = (short)va_arg((*arg), int);
04801                                                                       str.ConvertFromShort(value, param);
04802                                                                       end  = true;
04803                                                                     }
04804 
04805                                                                   if(mask[c] == __C('u')) 
04806                                                                     {
04807                                                                       XWORD value = (XWORD)va_arg((*arg), int);
04808                                                                       str.ConvertFromWord(value, param);
04809                                                                       end  = true;
04810                                                                     }
04811                                                                 }  
04812 
04813                                                               if((!shortint) && (!large))
04814                                                                 {
04815                                                                   if(mask[c] == __C('d')) 
04816                                                                     {
04817                                                                       int value = (int)va_arg((*arg), int);
04818                                                                       str.ConvertFromInt(value, param);
04819                                                                       end  = true;
04820                                                                     }
04821 
04822                                                                   if(mask[c] == __C('u')) 
04823                                                                     {
04824                                                                       XDWORD value = (XDWORD)va_arg((*arg), int);
04825                                                                       str.ConvertFromDWord(value, param);
04826                                                                       end  = true;
04827                                                                     }
04828                                                                 }  
04829 
04830                                                               if(large)
04831                                                                 {
04832                                                                   if(mask[c] == __C('d')) 
04833                                                                     {
04834                                                                       long long value = (long long)va_arg((*arg), long long);
04835                                                                       str.ConvertFromLongLong(value, param);
04836                                                                       end  = true;
04837                                                                     }
04838                                                               
04839                                                                   if(mask[c] == __C('u')) 
04840                                                                     {
04841                                                                       XQWORD value = va_arg((*arg), long long);
04842                                                                       str.ConvertFromLongLong(value, param);  
04843                                                                       end  = true;
04844                                                                     }
04845                                                                 }                                                                                                                                                                                       
04846                                                             }
04847                                                             break;
04848 
04849                                           case __C('f')   : { bool large = false;
04850                                                               for(int d=0; d<(int)XSTRING::GetSize(param); d++)
04851                                                                 {
04852                                                                   if(param[d] == __C('l')) large = true;
04853                                                                 }
04854 
04855                                                               if(!large)
04856                                                                 {  
04857                                                                   double data = va_arg((*arg), double);
04858                                                                   str.ConvertFromFloat((float)(data), param);
04859                                                                   end  = true;
04860                                                                 }
04861                                                                else                                                                
04862                                                                 { 
04863                                                                   double data = va_arg((*arg), double);
04864                                                                   str.ConvertFromDouble((double)data, param);
04865                                                                   end  = true;
04866                                                                 }                                                                   
04867                                                             }
04868                                                             break;
04869 
04870                                           case __C('g')   :
04871                                           case __C('G')   : { double data = va_arg((*arg), double);
04872 
04873                                                               str.ConvertFromDouble(data, param);
04874                                                               end  = true;
04875                                                             }
04876                                                             break;
04877 
04878                                           case __C('e')   :
04879                                           case __C('E')   :
04880 
04881                                           case __C('n')   :
04882                                           case __C('p')   : end = true;
04883                                                             break;
04884 
04885                                           case __C('s')   :
04886                                           case __C('S')   : { str = (XCHAR*)va_arg((*arg),XCHAR*);
04887 
04888                                                               XSTRING sizestr;
04889 
04890                                                               sizestr = param;
04891                                                               if(sizestr.GetSize()>2)
04892                                                                 {
04893                                                                   sizestr.ToUpperCase();
04894                                                                   sizestr.DeleteCharacter(__C('%'));
04895                                                                   sizestr.DeleteCharacter(__C('S'));
04896 
04897                                                                   if(!sizestr.IsEmpty())
04898                                                                     {
04899                                                                       int sizeadjust = sizestr.ConvertToInt();
04900                                                                       if(abs((int)sizeadjust) > (int)str.GetSize())
04901                                                                         {
04902                                                                           str.AdjustSize(abs((int)sizeadjust),(sizeadjust>0)?true:false, isspecialweb?__L("&nbsp"):__L(" "), isspecialweb);
04903                                                                         }
04904                                                                     }
04905                                                                 }
04906 
04907                                                               end = true;
04908                                                             }
04909                                                             break;
04910 
04911                                           case __C('%')   : str = __L("%");
04912                                                             end = true;
04913                                                             break;
04914 
04915                                           case __C('\x0') : end = true;
04916                                                             break;
04917 
04918                                               default     : break;
04919                                         }
04920 
04921                                       c++;
04922 
04923                                     } while(!end);
04924                                 }
04925                                 break;
04926 
04927               case __C('<')   : { openlabel = true;
04928                                   XCHAR character[2]={ mask[c], 0 };
04929                                   str.Set(character);
04930                                   c++;
04931                                 }
04932                                 break;
04933 
04934               case __C('>')   : { openlabel = false;
04935                                   XCHAR character[2]={ mask[c], 0 };
04936                                   str.Set(character);
04937                                   c++;
04938                                 }
04939                                 break;
04940 
04941               case 0x0A       : if(isspecialweb && (!openlabel))
04942                                   {
04943                                     str = __L("<br />");
04944                                   }
04945                                  else
04946                                   {
04947                                     XCHAR character[2]={ mask[c], 0 };
04948                                     str.Set(character);
04949                                   }
04950                                 c++;
04951                                 break;
04952 
04953               case 0x20       : if(isspecialweb && (!openlabel))
04954                                   {
04955                                     str = __L("&nbsp");
04956                                   }
04957                                  else
04958                                   {
04959                                     XCHAR character[2]={ mask[c], 0 };
04960                                     str.Set(character);
04961                                   }
04962                                 c++;
04963                                 break;
04964 
04965                  default      : { XCHAR   character[2]={ mask[c], 0 };
04966                                   XDWORD  value       = (XDWORD)mask[c];
04967 
04968                                   if(isspecialweb && (!openlabel) && (value > 128))
04969                                         str.Format(__L("&#%d"), value);
04970                                    else str.Set(character);
04971 
04972                                   c++;
04973                                 }
04974                                 break;
04975             }
04976 
04977           Add(str);
04978         }
04979 
04980       delete [] param;
04981     }
04982 
04983   return true;
04984 }
