05041 
05054 bool XSTRING::UnFormat(const XCHAR* mask,...)
05055 {
05056   if(!size)    return false;
05057   if(!mask)    return false;
05058   if(!mask[0]) return false;
05059   if(!text)    return false;
05060   if(!text[0]) return false;
05061 
05062   va_list arg;
05063   va_start(arg, mask);
05064 
05065   XCHAR* param = new XCHAR[16];
05066   if(param)
05067     {
05068       int   nparam;
05069       bool  end;
05070       int   indextext = 0;
05071       int   c         = 0;
05072       bool  inbrackets  = false;
05073 
05074       while(mask[c])
05075         {
05076           switch(mask[c])
05077             {
05078               case __C('%')   : memset(param,0,16*sizeof(XCHAR));
05079                                 param[0] = __C('%');
05080 
05081                                 nparam = 1;
05082                                 end    = false;
05083 
05084                                 c++;
05085 
05086                                 do{ param[nparam] = mask[c];
05087 
05088                                     nparam++;
05089 
05090                                     switch(mask[c])
05091                                       {
05092                                         case __C('c')   :
05093                                         case __C('C')   : { value_xchar = (XCHAR*)va_arg(arg, XCHAR*);
05094                                                             if(value_xchar) (*value_xchar) = Get()[indextext];
05095                                                             indextext++;
05096                                                             end  = true;
05097                                                           }
05098                                                           break;
05099 
05100                                         case __C('i')   :
05101                                         case __C('o')   :
05102                                         case __C('x')   :
05103                                         case __C('X')   : { value_int = (int*)va_arg(arg, int*);
05104 
05105                                                             if(value_int) (*value_int) = ConvertToInt(indextext,(XCHAR*)param, false);
05106                                                             end  = true;
05107                                                           }              
05108                                                           break;
05109 
05110                                         case __C('d')   :                                       
05111                                         case __C('u')   : { bool large    = false;
05112                                                             bool shortint = false;
05113     
05114                                                             for(int d=0; d<(int)XSTRING::GetSize(param); d++)
05115                                                               {
05116                                                                 if(param[d] == __C('l')) 
05117                                                                   {
05118                                                                     large = true;
05119                                                                   }
05120 
05121                                                                 if(param[d] == __C('h')) 
05122                                                                   {
05123                                                                     shortint = true;
05124                                                                   }
05125                                                               }
05126 
05127                                                             if(shortint)
05128                                                               {
05129                                                                 if(mask[c] == __C('d')) 
05130                                                                   {
05131                                                                     value_short = (short*)va_arg(arg, int*);
05132 
05133                                                                     if(value_short) (*value_short) = ConvertToInt(indextext,(XCHAR*)param, false);
05134                                                                     end  = true;
05135                                                                   }
05136 
05137                                                                 if(mask[c] == __C('u')) 
05138                                                                   {
05139                                                                     value_word = (XWORD*)va_arg(arg, int*);
05140 
05141                                                                     if(value_word) (*value_word) = ConvertToDWord(indextext,(XCHAR*)param, false);
05142                                                                     end  = true;
05143                                                                   }
05144                                                               }  
05145 
05146                                                             if((!shortint) && (!large))
05147                                                               {
05148                                                                 if(mask[c] == __C('d')) 
05149                                                                   {
05150                                                                     value_int = (int*)va_arg(arg, int*);
05151 
05152                                                                     if(value_int) (*value_int) = ConvertToInt(indextext,(XCHAR*)param, false);
05153                                                                     end  = true;
05154                                                                   }
05155 
05156                                                                 if(mask[c] == __C('u')) 
05157                                                                   {
05158                                                                     value_dword = (XDWORD*)va_arg(arg, int*);
05159 
05160                                                                     if(value_dword) (*value_dword) = ConvertToDWord(indextext,(XCHAR*)param, false);
05161                                                                     end  = true;
05162                                                                   }
05163                                                               }  
05164 
05165                                                             if(large)
05166                                                               {
05167                                                                 if(mask[c] == __C('d')) 
05168                                                                   {
05169                                                                     value_longlong = (long long*)va_arg(arg, long long*);
05170 
05171                                                                     if(value_longlong) (*value_longlong) = ConvertToInt(indextext,(XCHAR*)param, false);
05172                                                                     end  = true;
05173                                                                   }
05174 
05175                                                                 if(mask[c] == __C('u')) 
05176                                                                   {
05177                                                                     value_qword = (XQWORD*)va_arg(arg, long long*);
05178 
05179                                                                     if(value_qword) (*value_qword) = ConvertToDWord(indextext,(XCHAR*)param, false);
05180                                                                     end  = true;
05181                                                                   }
05182                                                               }                                                                                                                                                                                    
05183                                                           }
05184                                                           break;
05185 
05186                                         case __C('f')   : { bool large = false;
05187                                                             for(int d=0; d<(int)XSTRING::GetSize(param); d++)
05188                                                               {
05189                                                                 if(param[d] == __C('l')) large = true;
05190                                                               }
05191 
05192                                                             if(!large)
05193                                                               {  
05194                                                                 value_float = (float*)va_arg(arg, double*);
05195                                                                 if(value_float) (*value_float) = ConvertToFloat(indextext,(XCHAR*)param, false);
05196                                                                 end  = true;
05197                                                               }
05198                                                               else                                                                
05199                                                               { 
05200                                                                 value_double = (double*)va_arg(arg, double*);
05201                                                                 if(value_double) (*value_double) = ConvertToDouble(indextext,(XCHAR*)param, false);
05202                                                                 end  = true;
05203                                                               }                                                                   
05204                                                           }
05205                                                           break;
05206 
05207                                         case __C('g')   :
05208                                         case __C('G')   : { value_double = (double*)va_arg(arg, double*);
05209 
05210                                                             if(value_double) (*value_double) = ConvertToDouble(indextext,(XCHAR*)param, false);
05211 
05212                                                             end  = true;
05213                                                           }
05214                                                           break;
05215 
05216                                         case __C('e')   :
05217                                         case __C('E')   :
05218 
05219                                         case __C('n')   :
05220                                         case __C('p')   : end = true;
05221                                                           break;
05222 
05223                                         case __C('s')   :
05224                                         case __C('S')   : { value_xchar = (XCHAR*)va_arg(arg,XCHAR*);
05225                                                             if(!value_xchar) break;
05226 
05227                                                             XSTRING string;
05228 
05229                                                             int d = 0;
05230                                                             while(text[d+indextext]!=mask[c+1] /*&& text[d+indextext]!=__C(' ')*/)
05231                                                               {
05232                                                                 if(!text[indextext+d]) break;
05233                                                                 d++;
05234                                                               }
05235 
05236                                                             Copy(indextext, indextext+d, string);
05237 
05238                                                             if(nparam>2)
05239                                                               {
05240                                                                 XSTRING strsizeparam = param;
05241                                                                 int     sizeparam;
05242 
05243                                                                 strsizeparam.DeleteCharacters(0,1);
05244                                                                 strsizeparam.DeleteCharacters(strsizeparam.GetSize()-1,1);
05245 
05246                                                                 sizeparam = abs((int)strsizeparam.ConvertToInt());
05247                                                                 if(sizeparam)
05248                                                                   {
05249                                                                     if(string.GetSize()>(XDWORD)sizeparam)  string.DeleteCharacters(sizeparam, (string.GetSize()-sizeparam));
05250                                                                   }
05251                                                               }
05252 
05253                                                             memcpy((XBYTE*)value_xchar, (XBYTE*)string.Get(), (string.GetSize()*sizeof(XCHAR)));
05254                                                             value_xchar[string.GetSize()] = 0;
05255 
05256                                                             indextext += string.GetSize();
05257 
05258                                                             end = true;
05259                                                           }
05260                                                           break;
05261 
05262                                         case __C('%')   : end = true;
05263                                                           break;
05264 
05265                                         case __C('[')   : inbrackets  = true;
05266                                                           break;
05267 
05268                                         case __C(']')   : if(inbrackets)
05269                                                             {
05270                                                               value_xchar = (XCHAR*)va_arg(arg, XCHAR*);
05271 
05272                                                               ConvertStringWithMask(param, &text[indextext], value_xchar);
05273                                                               indextext += GetSize(value_xchar);
05274 
05275                                                               if((mask[c+1] == __C('s')) || (mask[c+1] == __C('S'))) c++;
05276                                                               end = true;
05277                                                             }
05278                                                           break;
05279 
05280                                         case __C('\x0') : end = true;
05281                                                           break;
05282 
05283                                             default     : break;
05284                                       }
05285 
05286                                     c++;
05287 
05288                                     if(end)
05289                                       {
05290                                         if(mask[c]!=__C('%'))
05291                                           {
05292                                             while(text[indextext] != mask[c])
05293                                               {
05294                                                 if(!text[indextext]) break;
05295                                                 indextext++;
05296                                               }
05297                                           }
05298                                          else
05299                                           {
05300                                             //if(text[indextext] ==__C(' ')) indextext++;
05301                                           }
05302                                       }
05303 
05304                                   } while(!end);
05305 
05306                               break;
05307 
05308                     default : c++;
05309                               indextext++;
05310                               break;
05311             }
05312         }
05313 
05314      delete [] param;
05315    }
05316 
05317   va_end(arg);
05318 
05319   return true;
05320 }
