05322 
05334 int XSTRING::FindCharacterFromSet(const XCHAR* chars)
05335 {
05336   XCHAR   c;
05337   XCHAR*  s;
05338   XCHAR*  p;
05339 
05340   for(s = text, c = *s; c != 0; s++, c = *s)
05341     {
05342       for(p = (XCHAR*)chars; *p != 0; p++)
05343         {
05344           if(c == *p)
05345             {
05346               return (int)(s - text);
05347             }
05348         }
05349     }
05350 
05351   return (int)(s - text);
05352 }
05353 
05354 
05369 bool XSTRING::AdjustSize(XDWORD size, bool ahead, XCHAR* characters, bool addstring)
05370 {
05371   if(this->size == size) return false;
05372 
05373   int diference;
05374 
05375   if(this->size > size)
05376     {
05377        diference = (this->size- size);
05378        DeleteCharacters(size,diference);
05379     }
05380    else
05381     {
05382       if(characters)
05383         {
05384           if(ahead)
05385             {
05386               XSTRING string;
05387 
05388               diference = (size-this->size);
05389               for(int c=0;c<diference;c++)
05390                 {
05391                   if(addstring) string.Add(characters); else string.Add(characters[0]);
05392                 }
05393 
05394               string+=Get();
05395 
05396               Set(string);
05397 
05398             }
05399            else
05400             {
05401               diference = ((size)-this->size);
05402               for(int c=0;c<diference;c++)
05403                 {
05404                   if(addstring) Add(characters);  else Add(characters[0]);
05405                 }
05406             }
05407         }
05408        else
05409         {
05410           diference = ((size)-this->size);
05411 
05412           ReAllocBuffer(size);
05413           if(ahead)
05414             {
05415               for(int c=0;c<diference;c++)
05416                 {
05417                   text[c+diference] = text[c];
05418                   text[c] = __C('-');
05419                 }
05420             }
05421         }
05422     }
05423 
05424   return true;
05425 }
05426 
05427 
05437 bool XSTRING::AdjustSize()
05438 {
05439   int c;
05440 
05441   for(c=0;c<(int)size;c++)
05442     {
05443       if(!text[c]) break;
05444     }
05445 
05446   if(!c)
05447     {
05448       return FreeBuffer();
05449     }
05450 
05451   return AdjustSize(c, false, __L(" "), false);
05452 }
