05466 bool XSTRING::BinaryCompare(XSTRING& string)
05467 {
05468   if(size != string.size)
05469     {
05470       return false;
05471     }
05472 
05473   return memcmp(text, string.text, size*sizeof(XCHAR)) == 0;
05474 }
