05476 
05489 bool XSTRING::Explode(XCHAR token, XVECTOR<XSTRING*>* receive)
05490 {
05491   int start = 0;
05492 
05493   do{ int end = FindCharacter(token,start);
05494       if(end==XSTRING_NOTFOUND) end = size;
05495 
05496       if((end-start)>0)
05497         {
05498           XSTRING* tok=new XSTRING();
05499           if(!tok)  return false;
05500 
05501           Copy(start, end, (*tok));
05502 
05503           if(!receive->Add(tok))
05504             {
05505               delete(tok);
05506               return false;
05507             }
05508         }
05509 
05510       start=end+1;
05511 
05512     } while(start < (int)size);
05513 
05514   return true;
05515 }
05516 
05517 
05529 bool XSTRING::GetTypeOfLineEnd(XSTRING& lineend)
05530 {
05531   //----------------
05532   // \n      x        0
05533   // \r      x        1
05534   // \n      \r       2
05535   // \r      \n       3
05536 
05537   int combinations[4] = { 0, 0, 0, 0 };
05538 
05539   if(GetSize()<2) return false;
05540 
05541   for(XDWORD c=0; c<GetSize()-1; c++)
05542     {
05543       if(Get()[c] == __C('\n'))
05544         {
05545           if(Get()[c+1] == __C('\r'))
05546             {
05547               combinations[2]++;
05548               c+=2;
05549 
05550             } else combinations[0]++;
05551         }
05552        else
05553         {
05554           if(Get()[c] == __C('\r'))
05555             {
05556               if(Get()[c+1] == __C('\n'))
05557                 {
05558                   combinations[3]++;
05559                   c+=2;
05560                 }
05561                else combinations[1]++;
05562             }
05563         }
05564     }
05565 
05566   if((combinations[0] >= combinations[1]) && (combinations[0] >= combinations[2]) && (combinations[0] >= combinations[3]))
05567     {
05568       lineend = __L("\n");
05569     }
05570    else
05571     {
05572       if((combinations[1] >= combinations[0]) && (combinations[1] >= combinations[2]) && (combinations[1] >= combinations[3]))
05573         {
05574           lineend = __L("\r");
05575         }
05576        else
05577         {
05578           if((combinations[2] >= combinations[0]) && (combinations[2] >= combinations[1]) && (combinations[2] >= combinations[3]))
05579             {
05580               lineend = __L("\n\r");
05581             }
05582            else
05583             {
05584               if((combinations[3] >= combinations[0]) && (combinations[3] >= combinations[1]) && (combinations[3] >= combinations[2]))
05585                 {
05586                   lineend = __L("\r\n");
05587                 }
05588             }
05589         }
05590     }
05591 
05592   return lineend.GetSize()?true:false;
05593 }
05594 
05595 
05607 bool XSTRING::ReAllocBuffer(XDWORD sizechar)
05608 {
05609   if(!sizechar)
05610     {
05611       FreeBuffer();
05612 
05613       return true;
05614     }
05615 
05616   XDWORD newsizechar      = (sizechar + XSTRING_EXCESSCHARS);
05617   XDWORD newsizecharbytes = (newsizechar * sizeof(XCHAR));
05618   XCHAR* newtext          = (XCHAR*)new XBYTE[newsizecharbytes];
05619   if(!newtext) 
05620     {
05621       return false;
05622     }
05623 
05624   memset(newtext, 0, newsizecharbytes);
05625 
05626   if(text)
05627     {
05628       if(this->size >= sizechar)
05629         { 
05630           memcpy(newtext, text, sizechar * sizeof(XCHAR));
05631         }
05632        else
05633         {
05634           memcpy(newtext, text, size * sizeof(XCHAR));
05635         } 
05636 
05637       FreeBuffer();
05638     }
05639 
05640   text          = newtext;
05641   this->size    = sizechar;
05642   this->sizemem = newsizechar;
05643 
05644   return true;
05645 }
05646 
05647 
05657 bool XSTRING::FreeBuffer()
05658 {
05659   if(xmutexfreemen) 
05660     {
05661       xmutexfreemen->Lock();
05662     }
05663 
05664   bool status = false;
05665 
05666   if(text) 
05667     {
05668       delete [] text;
05669 
05670       text    = NULL;
05671       size    = 0;
05672       sizemem = 0;
05673 
05674       status = true;
05675     }
05676 
05677   if(xmutexfreemen) 
05678     {
05679       xmutexfreemen->UnLock();
05680     }
05681 
05682   return status;
05683 }
