05685 
05699 bool XSTRING::ConvertStringWithMask(XCHAR* mask, XCHAR* string, XCHAR* result)
05700 {
05701   XSTRING  _mask;
05702   XSTRING  _string;
05703   XSTRING  _result;
05704 
05705   _mask   = mask;
05706   _string = string;
05707 
05708   char* resultchar = new char[XSTRING_MAXTEMPOSTR];
05709   if(!resultchar) return false;
05710 
05711   memset(resultchar, 0, XSTRING_MAXTEMPOSTR);
05712   
05713   XBUFFER maskchar;
05714   XBUFFER stringchar;
05715  
05716   _mask.ConvertToASCII(maskchar);
05717   _string.ConvertToASCII(stringchar);
05718  
05719   SSCANF(stringchar.GetPtrChar(), maskchar.GetPtrChar(), resultchar, XSTRING_MAXTEMPOSTR);
05720 
05721   _result = resultchar;
05722 
05723   memcpy(result, _result.Get(), ((_result.GetSize()+1)*sizeof(XCHAR)));
05724 
05725   return true;
05726 }
05727 
05728 
05742 void XSTRING::Base64_EncodeBlock(XBYTE *in, XBYTE *out, int len)
05743 {
05744   out[0] = (XBYTE) codetablebase64[ (int)(in[0] >> 2) ];  
05745   out[1] = (XBYTE) codetablebase64[ (int)(((in[0] & 0x03) << 4) | ((in[1] & 0xf0) >> 4)) ];
05746   out[2] = (XBYTE) (len > 1 ? codetablebase64[ (int)(((in[1] & 0x0f) << 2) | ((in[2] & 0xc0) >> 6)) ] : '=');
05747   out[3] = (XBYTE) (len > 2 ? codetablebase64[ (int)(in[2] & 0x3f) ] : '=');
05748 }
05749 
05750 
05764 bool XSTRING::Base64_Encode(XBYTE* buffer, XDWORD buffersize, XSTRING& line)
05765 {
05766   XBYTE in[3]     = { 0 };
05767     XBYTE out[4]    = { 0 };
05768   int   i;
05769   int   len;
05770   int   cin       = 0;
05771   
05772     *in   = (XBYTE) 0;
05773     *out  = (XBYTE) 0;
05774 
05775   while((XDWORD)cin < buffersize) 
05776     {
05777       len = 0;
05778       for(i = 0; i < 3; i++) 
05779         {
05780           in[i] = (XBYTE)buffer[cin++];
05781 
05782           if((XDWORD)cin <= buffersize) 
05783             {
05784               len++;
05785             }
05786            else 
05787             {
05788               in[i] = (XBYTE)0;
05789             }
05790         }
05791 
05792       if(len > 0) 
05793         {
05794           Base64_EncodeBlock(in, out, len );
05795           line.Add(out, 4);                 
05796         }      
05797     }
05798 
05799   return true;
05800 }
05801 
05802 
05815 void XSTRING::Base64_DecodeBlock(XBYTE in[4], XBYTE out[3]) 
05816 {
05817   out[0] = (XBYTE)(in[0] << 2 | in[1] >> 4);
05818   out[1] = (XBYTE)(in[1] << 4 | in[2] >> 2);
05819   out[2] = (XBYTE)(((in[2] << 6) & 0xc0) | in[3]);
05820 }
05821 
05822 
05836 bool XSTRING::Base64_Decode(const char* in_buffer, int in_buffer_size, XBUFFER& outbuffer) 
05837 {
05838   XBYTE       in[4]; 
05839   XBYTE       out[3];
05840   XBYTE       v;
05841   int         i;
05842   int         len;    
05843   const char* ptr  = in_buffer;
05844     
05845   while (ptr <= in_buffer + in_buffer_size) 
05846     {
05847       for(len = 0, i = 0; i < 4 && (ptr <= in_buffer + in_buffer_size); i++) 
05848         {
05849           v = 0;
05850           while((ptr <= in_buffer + in_buffer_size) && v == 0) 
05851             {
05852               v = (XBYTE)ptr[0];
05853               ptr++;
05854               v = (XBYTE)((v < 43 || v > 122) ? 0 : decodetablebase64[v - 43]);
05855               if(v) v = (XBYTE)((v == '$') ? 0 : v - 61);
05856             }
05857 
05858           if(ptr <= in_buffer + in_buffer_size) 
05859             {
05860               len++;
05861               if(v) in[i] = (XBYTE)(v - 1);
05862             } 
05863            else 
05864             {
05865               in[i] = 0;
05866             }
05867         }
05868 
05869       if(len) 
05870         {
05871           Base64_DecodeBlock(in, out);
05872 
05873           for(i = 0; i < len - 1; i++) 
05874             {
05875               outbuffer.Add(out[i]);              
05876             }
05877         }
05878     }
05879     
05880   return outbuffer.GetSize()?true:false;
05881 }
