05894 void XSTRING::Clean()
05895 {
05896   text            = NULL;
05897   size            = 0;
05898   sizemem         = 0;
05899 
05900   xmutexfreemen   = NULL;
05901 
05902   value_short     = NULL;
05903   value_word      = NULL;
05904   value_int       = NULL;
05905   value_dword     = NULL;
05906   value_longlong  = NULL;
05907   value_qword     = NULL;
05908   value_float     = NULL;
05909   value_double    = NULL;
05910   value_xchar     = NULL;
05911 }
