00748 int XSYSTEM::IsLineInHostFile(XFILETXT& xfileTXT, XCHAR* domain, XCHAR* IP)
00749 {
00750   bool found = false;    
00751   int  c     = 0;
00752 
00753   if(!xfileTXT.GetNLines()) return XSTRING_NOTFOUND;
00754 
00755   do{ XSTRING* line = xfileTXT.GetLine(c); 
00756       if(line)
00757         {
00758           int index = line->Find(domain, true);  
00759           if(index != XSTRING_NOTFOUND) 
00760             {     
00761               found = true;
00762 
00763               for(XDWORD d=0; d<line->GetSize(); d++)
00764                 {
00765                   XCHAR character = line->Get()[d];
00766 
00767                   if((character != __C('\t') && (character != __C(' '))))     
00768                     {
00769                       if(character == __C('#'))     
00770                         {
00771                           found = false;
00772                         }
00773 
00774                       break;
00775                     }               
00776                 }
00777                       
00778               if(found) break; 
00779             }
00780         }
00781 
00782       c++;
00783 
00784     } while(c<xfileTXT.GetNLines());
00785 
00786   if(!found) return XSTRING_NOTFOUND;
00787 
00788   return c;
00789 }
