01017 bool XTRACE::SetTarget(int index, XTRACE_TYPE type, XCHAR* aim)
01018 {
01019   if(index >= XTRACE_MAXNTARGETS) return false;
01020 
01021   if(!aim) return false;
01022 
01023   targets[index].SetType(type);
01024 
01025   if(type == XTRACE_TYPE_NET)
01026     {
01027       #if(defined(DIO_ACTIVE) && defined(DIO_STREAMUDP_ACTIVE))
01028 
01029       XSTRING string;
01030       XSTRING url;
01031       int     port;
01032 
01033       string = aim;
01034       url.AdjustSize(_MAXSTR);
01035       string.UnFormat(__L("%s:%d"), url.Get(), &port);
01036       url.AdjustSize();
01037 
01038       targets[index].SetAim(url.Get());
01039       targets[index].SetPort(port);
01040       targets[index].IPTarget();
01041 
01042       if(targets[index].GetNETHandle()) CloseHandleNet(&targets[index]);
01043       GetHandleNet(&targets[index]);
01044 
01045       if(!localIP)    ObtainLocalIP();
01046 
01047       #ifndef XTRACE_NOINTERNET
01048       if(!publicIP)   ObtainPublicIP();
01049       #endif
01050 
01051       #endif
01052     }
01053    else
01054     {
01055       targets[index].SetAim(aim);
01056     }
01057 
01058   return true;
01059 }
