01376 bool XTRACE::Print(XBYTE level, XCHAR* mask,...)
01377 {
01378   XSTRING string;
01379   XSTRING outstring;
01380 
01381   string.Add(applicationname);
01382   if(string.GetSize())
01383     {
01384       string.AdjustSize(XTRACE_MAXAPPLICATIONNAMESIZE, false, __L(" "));
01385       outstring += string;
01386       outstring += __L(" ");
01387     }
01388 
