01410 
01411   va_end(arg);
01412 
01413   sequence++;
01414   
01415   if(level)
01416     {
01417       if((level & XTRACE_LEVEL_WITHCOLOR) == XTRACE_LEVEL_WITHCOLOR)
01418         {
01419 
01420         }
01421         else
01422         {
01423           if((level & XTRACE_LEVEL_WITHTAB) == XTRACE_LEVEL_WITHTAB)
01424             {
01425               XSTRING tabstr;
01426               GenerateTab(level&0x0F, tabstr);
01427 
01428               string.Insert(tabstr, 0);
01429 
01430             }
