01437                   codestr.Format(__L("%02d: "), (level&0x0F));
01438 
01439                   string.Insert(codestr, 0);
01440                 }
01441             }
01442         }
01443     }
01444   
01445   outstring += string;
01446 
01447   for(int c=0; c<XTRACE_MAXNTARGETS ; c++)
01448     {
01449       switch(targets[c].GetType())
01450         {
01451           case XTRACE_TYPE_SPECIAL   : PrintSpecial(&targets[c], level, outstring.Get());  break;
01452           case XTRACE_TYPE_FILE      : PrintFile(&targets[c], level, outstring.Get());     break;
01453           case XTRACE_TYPE_NET       : PrintNet(&targets[c], level, outstring.Get());      break;
01454                           default     : break;
01455         }
01456     }
01457 
