01526           string += __L(" ");
01527 
01528           for(XDWORD c=0; c<_sizeline; c++)
01529             {
01530               XCHAR character = (XCHAR)data[index];
01531 
01532               if((character<0x20) || (character>0x80) || (character==__C('%')) || (character==__C('\\'))) character = __C('.');
01533 
01534               strdata.Format(__L("%c"),character);
01535               string += strdata;
01536               index++;
01537             }
01538         }
01539 
01540       Print(level, string.Get());
01541 
01542       if(_size>XTRACE_MAXSIZEDATABLOCK) break;
01543     }
01544 
01545   return true;
01546 }
