01843 XDWORD XTRACE::GetTraceFromXBuffer(XBUFFER& xbufferpacket, XDWORD& publicIP, XDWORD& localIP, XBYTE& level, XDWORD& sequence, XDATETIME* xtime, XBUFFER& data)
01844 {
01845   if(!xtime)                   return 1;
01846   if(!xbufferpacket.GetSize()) return 1;
01847 
01848   XDWORD IDpacket = XTRACE_IDPACKET;
01849   
01850   XDWORD    sizepacketread  = 0;
01851   XDWORD    dword;
01852   XDWORD    sizeinbuffer    = xbufferpacket.GetSize();
01853   XBUFFER   xbuffer(false);
01854   XDWORD    sizeheader      = (sizeof(XDWORD)    +    // ID.
01855                                sizeof(XDWORD)    +    // SizePacket.
01856                                sizeof(XDWORD)    +    // public IP address origin.
01857                                sizeof(XDWORD)    +    // local  IP address origin.
01858                                sizeof(XBYTE)     +    // Level.
01859                                sizeof(XDWORD)    +    // Sequence.
01860                               (sizeof(XDWORD)*7) +    // Date Time.
01861                                sizeof(XWORD));        // CRC16
01862 
01863   if(!sizeinbuffer)             return 1;
01864   if(sizeinbuffer < sizeheader) return 1;
01865 
01866 
01867   while(sizeinbuffer-4)
01868     {
01869       xbufferpacket.Get(IDpacket, 0);
01870       if(IDpacket == XTRACE_IDPACKET) break;
01871      
01872       xbufferpacket.Extract(NULL, 0, 1);
01873       sizeinbuffer--;
01874     }
01875 
01876   if(IDpacket != XTRACE_IDPACKET) return 1;
01877 
01878   if(!sizeinbuffer)               return 1;
01879   if(sizeinbuffer < sizeheader)   return 1;
01880 
01881   if(!xbufferpacket.Get(sizepacketread, sizeof(IDpacket)))
01882     {
01883       return 1;
01884     }
01885 
01886   if(!sizepacketread)
01887     {
01888       return 2;
01889     }
01890 
01891   if(sizepacketread > sizeinbuffer)
01892     {
01893       return 1;
01894     }
01895 
01896  
01897   XDWORD sizeassing    = sizepacketread;
01898   XDWORD sizeassingmin = 5*1024;
01899 
01900   if(sizeassing < sizeassingmin) sizeassing = sizeassingmin;
01901   xbuffer.Resize(sizeassing);
01902 
01903   xbufferpacket.Extract(xbuffer.Get(), 0, sizepacketread);
01904 
01905   XWORD crc16;
01906   XTRACE_CRC16(xbuffer.Get(), (xbuffer.GetSize()-sizeof(XWORD)), crc16)
01907 
01908   XWORD crc16read;
01909 
01910   xbuffer.Get(crc16read, xbuffer.GetSize()-sizeof(XWORD));
01911   if(crc16 != crc16read)
01912     {
01913       return 2;
01914     }
01915 
01916   xbuffer.SetPosition(0);
01917 
01918   xbuffer.Get(IDpacket);
01919   xbuffer.Get(sizepacketread);
01920   xbuffer.Get(publicIP);
01921   xbuffer.Get(localIP);
01922   xbuffer.Get(level);
01923   xbuffer.Get(sequence);
01924 
01925   xbuffer.Get(dword); xtime->SetYear(dword);
01926   xbuffer.Get(dword); xtime->SetMonth(dword);
01927   xbuffer.Get(dword); xtime->SetDay(dword);
01928   xbuffer.Get(dword); xtime->SetHours(dword);
01929   xbuffer.Get(dword); xtime->SetMinutes(dword);
01930   xbuffer.Get(dword); xtime->SetSeconds(dword);
01931   xbuffer.Get(dword); xtime->SetMilliSeconds(dword);
01932 
01933   XDWORD sizestr = sizepacketread - sizeheader;        // CRC16  
01934   data.Empty();
01935   data.Add(&xbuffer.Get()[xbuffer.GetPosition()], sizestr);
01936 
01937 
01938   return 0;
01939 }
