02015 bool XTRACE::SetTraceToXBuffer(XDWORD publicIP, XDWORD localIP, XBYTE level, XDWORD sequence, XDATETIME* xtime, XDWORD& sizepacket, XBUFFER& xbufferpacket)
02016 {
02017   if(!xtime)  return false;
02018     
02019   xbufferpacket.Empty();
02020 
02021   XDWORD IDpacket = XTRACE_IDPACKET;
02022  
02023   sizepacket += sizeof(XDWORD) +                        // ID.
02024                 sizeof(XDWORD) +                        // SizePacket.
02025                 sizeof(XDWORD) +                        // public IP address origin.
02026                 sizeof(XDWORD) +                        // local  IP address origin.
02027                 sizeof(XBYTE)  +                        // Level.
02028                 sizeof(XDWORD) +                        // Sequence.
02029                 (sizeof(XDWORD)*7) +                    // Date Time.                
02030                 sizeof(XWORD);                          // CRC16
02031 
02032   xbufferpacket.Resize(sizepacket);
02033 
02034   xbufferpacket.Set(IDpacket);  
02035   xbufferpacket.Set(sizepacket);
02036   xbufferpacket.Set(publicIP);
02037   xbufferpacket.Set(localIP);
02038   xbufferpacket.Set(level);
02039   xbufferpacket.Set(sequence);
02040 
02041   xbufferpacket.Set((XDWORD)xtime->GetYear());
02042   xbufferpacket.Set((XDWORD)xtime->GetMonth());
02043   xbufferpacket.Set((XDWORD)xtime->GetDay());
02044   xbufferpacket.Set((XDWORD)xtime->GetHours());
02045   xbufferpacket.Set((XDWORD)xtime->GetMinutes());
02046   xbufferpacket.Set((XDWORD)xtime->GetSeconds());
02047   xbufferpacket.Set((XDWORD)xtime->GetMilliSeconds());
02048 
02049   return true;  
02050 }
