02070 bool XTRACE::SetTraceTextToXBuffer(XDWORD publicIP, XDWORD localIP, XBYTE level, XDWORD sequence, XDATETIME* xtime, XCHAR* string, XBUFFER& xbufferpacket)
02071 {     
02072   if(!string) return false;
02073   
02074   XSTRING  line;
02075   XBUFFER  bufferline;
02076 
02077   line = string;  
02078   line.ConvertToExchangeXBuffer(bufferline);
02079   
02080   XDWORD sizepacket = bufferline.GetSize();    // Line (WORD) each character.
02081   
02082   if(!SetTraceToXBuffer(publicIP, localIP, level, sequence, xtime, sizepacket, xbufferpacket)) return false;
02083   
02084   xbufferpacket.Set(bufferline.Get(), bufferline.GetSize());
02085   
02086   XWORD crc16;
02087   XTRACE_CRC16(xbufferpacket.Get(), (xbufferpacket.GetSize()-sizeof(XWORD)), crc16)
02088 
02089   xbufferpacket.Set((XWORD)crc16);
02090     
02091   return true; 
02092 }
