02121 bool XTRACE::ObtainLocalIP()
02122 {
02123   bool status = false;
02124 
02125   #if(defined(DIO_ACTIVE) && defined(DIO_STREAMUDP_ACTIVE))
02126 
02127   DIOSTREAMIPLOCALENUMDEVICES* enumdevices = (DIOSTREAMIPLOCALENUMDEVICES*)GEN_DIOFACTORY.CreateStreamEnumDevices(DIOSTREAMENUMTYPE_IP_LOCAL);
02128   if(enumdevices)
02129     {
02130       if(!localIPfilter)
02131         {
02132           DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)enumdevices->GetFirstActiveDevice();
02133           if(device)
02134             {
02135               XSTRING string;
02136 
02137               device->GetIP()->GetXString(string);
02138               SetLocalIPString(string.Get());
02139 
02140               status = true;
02141             }
02142         }
02143        else
02144         {
02145           enumdevices->Search();
02146 
02147           for(XDWORD c=0; c<enumdevices->GetDevices()->GetSize(); c++)
02148             {
02149               DIOSTREAMDEVICEIP* device = (DIOSTREAMDEVICEIP*)enumdevices->GetDevices()->Get(c);
02150               if(device)
02151                 {
02152                   //device->DebugPrintInfo();
02153 
02154                   if((!device->GetIP()->IsEmpty()) &&
02155                      ((device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_ETHERNET) || (device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_WIFI) || (device->GetIPType() == DIOSTREAMIPDEVICE_TYPE_PPP)) &&
02156                       (device->GetIP()->Get()[0] == localIPfilter))
02157                     {
02158                       XSTRING string;
02159 
02160                       device->GetIP()->GetXString(string);
02161                       SetLocalIPString(string.Get());
02162                       status = true;
02163                     }
02164                 }
02165             }
02166         }
02167 
02168       GEN_DIOFACTORY.DeleteStreamEnumDevices(enumdevices);
02169     }
02170 
02171   #endif
02172 
02173   return status;
02174 }
