02188 bool XTRACE::SetLocalIPString(XCHAR* localIPstring)
02189 {
02190   XSTRING _localIPstring;
02191   int data[4];
02192 
02193   _localIPstring = localIPstring;
02194   _localIPstring.UnFormat(__L("%d.%d.%d.%d"), &data[0], &data[1], &data[2], &data[3]);
02195 
02196   localIP  |= (data[0] << 24);
02197   localIP  |= (data[1] << 16);
02198   localIP  |= (data[2] << 8);
02199   localIP  |= (data[3]);
02200 
02201   return true;
02202 }
