00205 bool XTRANSLATION::Translate_Add(XDWORD ID, XCHAR* sentence, XDWORD fixed)
00206 {
00207   if(!ID)       return false;
00208   if(!sentence) return false;
00209 
00210   XDWORD sizesentence = XSTRING::GetSize(sentence);
00211   if(fixed)
00212     {
00213       if(sizesentence > fixed) sizesentence = fixed;
00214     }
00215 
00216   XCHAR* newsentence = new XCHAR[sizesentence+1];
00217   if(newsentence)
00218     {
00219       memset(newsentence, 0        , (sizesentence+1) * sizeof(XCHAR));
00220       memcpy(newsentence, sentence ,  sizesentence    * sizeof(XCHAR));
00221 
00222       sentences[ID] = newsentence;
00223     }
00224 
00225   return true;
00226 }
