00251 bool XTRANSLATION_GEN::Sentence_Add(XDWORD ID, XDWORD codelanguage, XCHAR* sentence, XDWORD fixed)
00252 {
00253   if(!ID)           return false;
00254   if(!codelanguage) return false;
00255   if(!sentence)     return false;
00256 
00257   if(Sentence_Get(ID, codelanguage)) return false;
00258 
00259   XTRANSLATION_GEN_SENTENCE* sentenceGEN = new XTRANSLATION_GEN_SENTENCE();
00260   if(!sentenceGEN) return false;
00261 
00262   int sizesentence = XSTRING::GetSize(sentence);
00263 
00264   sentenceGEN->ID           = ID;
00265   sentenceGEN->codelanguage = codelanguage;
00266   sentenceGEN->sentence     = new XCHAR[sizesentence+1];
00267   sentenceGEN->fixed        = fixed;
00268   if(!sentenceGEN->sentence)
00269     {
00270       delete sentenceGEN;
00271       return false;
00272     }
00273 
00274   memset(sentenceGEN->sentence,        0, (sizesentence+1) * sizeof(XCHAR));
00275   memcpy(sentenceGEN->sentence, sentence, sizesentence     * sizeof(XCHAR));
00276 
00277   sentences.Add(sentenceGEN);
00278 
00279   return true;
00280 }
