00295 XTRANSLATION_GEN_SENTENCE* XTRANSLATION_GEN::Sentence_Get(XDWORD ID, XDWORD codelanguage)
00296 {
00297   for(XDWORD c=0; c<sentences.GetSize(); c++)
00298     {
00299       XTRANSLATION_GEN_SENTENCE* sentence = sentences.Get(c);
00300       if(sentence)
00301         {
00302           if((sentence->ID == ID) && (sentence->codelanguage == codelanguage)) return sentence;
00303         }
00304     }
00305 
00306   return NULL;
00307 }
