00458 
00470 bool XTRANSLATION_GEN::Sentences_AddToTranslation(XDWORD codelanguage)
00471 {
00472   bool existsentenceGENlanguage = false;
00473 
00474   for(XDWORD c=0; c<sentences.GetSize(); c++)
00475     {
00476       XTRANSLATION_GEN_SENTENCE* sentenceGEN = sentences.Get(c);
00477       if(sentenceGEN)
00478         {
00479           if(sentenceGEN->codelanguage == codelanguage)
00480             {
00481               GEN_XTRANSLATION.Translate_Add(sentenceGEN->ID, sentenceGEN->sentence, sentenceGEN->fixed);
00482               existsentenceGENlanguage = true;
00483             }
00484         }
00485     }
00486 
00487   return existsentenceGENlanguage;
00488 }
