00102 bool XWINDOWSDRIVEIMAGEMANAGER::GetDrives(XVECTOR<XDRIVEIMAGEMANAGER_DRIVE*>& drives)
00103 {
00104   drives.DeleteContents();
00105   drives.DeleteAll();
00106 
00107   XPATH   path;
00108   XDWORD  drivemask = GetLogicalDrives();
00109   XQWORD  ID        = 0;
00110   int     c         = 0;
00111 
00112   // GetLogicalDrives returns 0 on failure, or a bitmask representing the drives available on the system (bit 0 = A:, bit 1 = B:, etc)
00113 
00114   while(drivemask != 0)
00115     {
00116       if(drivemask & 1)
00117         {
00118           // the "A" in path will get incremented by the # of bits
00119           // we've shifted
00120           path.Format( __L("\\\\.\\%c:\\"), __C('A') + c);
00121           if(CheckDriveType(path, ID))
00122             {
00123               XDRIVEIMAGEMANAGER_DRIVE* drive = new XDRIVEIMAGEMANAGER_DRIVE();
00124               if(drive)
00125                 {
00126                   XSTRING label;
00127 
00128                   drive->GetName()->Format(__L("%c:"), __C('A') + c);
00129                   drive->GetPath()->Set(path);
00130                   drive->SetVolume(c);
00131                   drive->GetLabel()->Set(label);
00132                   drive->SetID(ID);
00133 
00134                   if(GetDriveLabel(drive->GetName()->Get(), label))  drive->GetLabel()->Set(label);
00135 
00136                   drives.Add(drive);
00137                 }
00138             }
00139         }
00140 
00141       drivemask >>= 1;
00142 
00143       ++c;
00144     }
00145 
00146   return true;
00147 }
