00161 bool XWINDOWSDRIVEIMAGEMANAGER::Open(XDRIVEIMAGEMANAGER_DRIVE& drive)
00162 {
00163   HANDLE handlevolume = GetHandleFromVolume(drive.GetVolume(), GENERIC_READ | GENERIC_WRITE);
00164   if(handlevolume == INVALID_HANDLE_VALUE) return false;
00165 
00166   drive.SetHandle((XDWORD)handlevolume);
00167 
00168   XDWORD deviceID = 0;
00169   if(!GetDeviceID(handlevolume, deviceID)) return false;
00170   drive.SetDeviceID(deviceID);
00171 
00172   if(!LockVolume(handlevolume, true))  return false;
00173 
00174   if(!UnMountVolume(handlevolume))    return false;
00175 
00176   HANDLE rawhandle = GetHandleFromDevice(deviceID, GENERIC_READ | GENERIC_WRITE);
00177   if(rawhandle == INVALID_HANDLE_VALUE) return false;
00178 
00179   drive.SetRawHandle((XDWORD)rawhandle);
00180 
00181   XQWORD sectorsize    = 0;
00182   XQWORD numbersectors = 0;
00183 
00184   if(!GetNumberOfSectors(rawhandle, sectorsize, numbersectors)) return false;
00185 
00186   drive.SetSectorSize(sectorsize);
00187   drive.SetNumberSectors(numbersectors);
00188 
00189   return true;
00190 }
