00207 bool XWINDOWSDRIVEIMAGEMANAGER::Read(XDRIVEIMAGEMANAGER_DRIVE& drive, XQWORD startsector, XQWORD numbersectors, XBUFFER& buffer)
00208 {
00209   XBYTE* _buffer;
00210   XDWORD maxbuffer         = ((XDWORD)numbersectors * (XDWORD)drive.GetSectorSize());
00211   XQWORD maxnsectorsblock  = (maxbuffer / drive.GetSectorSize());
00212   XQWORD readnsectorsblock = 0;
00213   XQWORD sizeread          = 0;
00214   bool   status            = false;
00215 
00216   buffer.Delete();
00217 
00218   _buffer = new XBYTE[maxbuffer];
00219   if(!_buffer) return false;
00220 
00221   if(maxnsectorsblock > numbersectors) maxnsectorsblock = numbersectors;
00222 
00223   while(readnsectorsblock < numbersectors)
00224     {
00225       XDRIVEIMAGEMANAGER_XEVENT xevent(this, XDRIVEIMAGEMANAGER_XEVENT_TYPE_READSECTORS);
00226 
00227       xevent.SetDrive(&drive);
00228       xevent.SetIndexSector(startsector);
00229       xevent.SetNumberSectorsInOperation(maxnsectorsblock);
00230 
00231       sizeread = (maxnsectorsblock * drive.GetSectorSize());
00232 
00233       memset(_buffer, 0, maxbuffer);
00234 
00235       status = ReadSectorDataFromHandle((HANDLE)drive.GetRawHandle(), startsector, drive.GetSectorSize(), _buffer, sizeread);
00236       if(status)
00237         {
00238           xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_NONE);
00239 
00240           buffer.Add(_buffer, (XDWORD)sizeread);
00241 
00242           readnsectorsblock += maxnsectorsblock;
00243 
00244           if((readnsectorsblock + maxnsectorsblock) > numbersectors)             maxnsectorsblock = (numbersectors - readnsectorsblock);
00245           if((readnsectorsblock + maxnsectorsblock) > drive.GetNumberSectors())  maxnsectorsblock = (drive.GetNumberSectors() - readnsectorsblock);
00246         }
00247        else
00248         {
00249           xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_READSECTORS);
00250         }
00251 
00252       PostEvent(&xevent);
00253 
00254       if(!status) break;
00255 
00256     }
00257 
00258   delete [] _buffer;
00259 
00260   return status;
00261 }
