00719 bool XWINDOWSDRIVEIMAGEMANAGER::GetMediaType(HANDLE hdevice)
00720 {
00721   DISK_GEOMETRY diskgeometry;
00722   DWORD         nbytesreturned;
00723 
00724   if(DeviceIoControl(hdevice, IOCTL_DISK_GET_DRIVE_GEOMETRY,NULL, 0, &diskgeometry, sizeof(diskgeometry), &nbytesreturned, NULL))
00725     {
00726       if((diskgeometry.MediaType == FixedMedia) || (diskgeometry.MediaType == RemovableMedia)) return true; // Not a floppy
00727     }
00728 
