00482     {
00483       case XWINDOWSFILEMODE_READONLY  : fd = _open_osfhandle((intptr_t)filehandle, _O_RDONLY);
00484                                         if (fd == -1) return NULL;
00485                                         fp = _fdopen(fd, "rb");                                        
00486                                         break;
00487 
00488       case XWINDOWSFILEMODE_READWRITE : fd = _open_osfhandle((intptr_t)filehandle, _O_RDWR);
00489                                         if (fd == -1) return NULL;
00490                                         fp = _fdopen(fd, "r+b");
00491                                         break;
00492 
00493       case XWINDOWSFILEMODE_CREATE    : fd = _open_osfhandle((intptr_t)filehandle, _O_CREAT);
00494                                         if (fd == -1) return NULL;
