00531 bool XWINDOWSFILE::ActualizeSize()
00532 {
00533   if(!isopen)       return false;
00534   if(!filehandle)   return false;
00535 
00536   if(!isreadonly)
00537     {
00538       if(!Flush()) return false;
00539     }
00540 
00541   LARGE_INTEGER exsize;
00542   if(GetFileSizeEx(filehandle, &exsize) == INVALID_FILE_SIZE) return false;
00543 
00544   cachesize  = exsize.HighPart;
00545   cachesize <<=32;
00546   cachesize |= exsize.LowPart;
00547 
00548   return true;
00549 }
