00228     {      
00229       _command.Format(__L("\"%s\""), applicationpath);
00230       
00231       if(params) _command.AddFormat(__L(" %s"), params);
00232       
00233       XBUFFER charstr;
00234       
00235       _command.ConvertToASCII(charstr);
00236       memcpy(cmdline, charstr.GetPtrChar(), _command.GetSize()+1);
00237       
00238       memset(&saattr, 0, sizeof(saattr));
00239       saattr.nLength              = sizeof(SECURITY_ATTRIBUTES);
00240       saattr.bInheritHandle       = TRUE;
00241       saattr.lpSecurityDescriptor = NULL;
00242 
00243       // Create a pipe for the child process's STDOUT.
00244       if(!CreatePipe(&stdhandle_out_read, &stdhandle_out_write, &saattr, 0))  return false;
00245       // Ensure the read handle to the pipe for STDOUT is not inherited.
00246       if(!SetHandleInformation(stdhandle_out_read, HANDLE_FLAG_INHERIT, 0))   return false;
00247 
00248       // Create a pipe for the child process's STDIN.
00249       if(!CreatePipe(&stdhandle_in_read, &stdhandle_in_write, &saattr, 0))    return false;
00250       // Ensure the read handle to the pipe for STDIN is not inherited.
00251       if(!SetHandleInformation(stdhandle_in_write, HANDLE_FLAG_INHERIT, 0))   return false;
00252 
00253       ZeroMemory(&processinfo, sizeof(PROCESS_INFORMATION));
00254 
00255       ZeroMemory(&startupinfo, sizeof(STARTUPINFO));
00256       startupinfo.cb          = sizeof(startupinfo);
00257       startupinfo.hStdError   = stdhandle_out_write;
00258       startupinfo.hStdOutput  = stdhandle_out_write;
00259       startupinfo.hStdInput   = stdhandle_in_read;    // GetStdHandle(STD_INPUT_HANDLE);
00260       startupinfo.dwFlags    |= STARTF_USESTDHANDLES;
00261 
00262       if(CreateProcessA(NULL, cmdline, NULL, NULL, TRUE, CREATE_NO_WINDOW, NULL, NULL, &startupinfo, &processinfo))
00263         {
00264           CloseHandle(stdhandle_out_write);
00265           
00266           if(in)
00267             {
00268               if(in->GetSize())
00269                 {
00270                   DWORD bytes_write;
00271 
00272                   XBUFFER charstr;
00273                   
00274                   (*in).ConvertToASCII(charstr); 
00275                   WriteFile(stdhandle_in_write, charstr.Get(), in->GetSize(), &bytes_write, NULL);
00276                   
00277                   FlushFileBuffers(stdhandle_in_write);
00278                 }
00279             }
00280 
00281           CloseHandle(stdhandle_in_write);
00282 
00283           if(out)
00284             {
00285               strcpy(outbuf, "");
00286 
00287               for(;;)
00288                 {
00289                   if(!ReadFile(stdhandle_out_read, tempbuf, TEMPBUF_SIZE-1, &bytes_read, NULL))
00290                     {
00291                       break;
00292                     }
00293 
00294                   if(bytes_read > 0)
00295                     {
00296                       tempbuf[bytes_read] = '\0';
00297                       strcat(outbuf, tempbuf);
00298                     }
00299                 }
00300 
00301               (*out) = outbuf;
00302             }
00303 
00304           if(WaitForSingleObject(processinfo.hProcess, (out?INFINITE:500)) == WAIT_OBJECT_0)
00305             {
00306               if(out)
00307                 {
00308                   if(GetExitCodeProcess(processinfo.hProcess, &exitcode))
00309                     {
00310                       if(returncode) (*returncode) = exitcode;
00311                     }
00312 
00313                   CloseHandle(processinfo.hProcess);
00314                   CloseHandle(processinfo.hThread);
00315                 }
00316 
00317               status = true;
00318             }
00319         }
00320     }
00321 
00322   if(cmdline) delete [] cmdline;
00323   if(outbuf)  delete [] outbuf;
00324   if(tempbuf) delete [] tempbuf;
00325 
00326   return status;
00327 }
