00612   if(!sizetitlewindow)
00613     { 
00614       return false;
00615     }
00616                   
00617   title.AdjustSize(sizetitlewindow+1);
00618   int size = GetWindowText(hwnd, title.Get(), sizetitlewindow + 1);
00619   title.AdjustSize();
00620 
00621   if(!size) 
00622     {
00623       title.Empty(); 
00624       return false;
00625     }
00626 
00627   RECT wrect;
00628 
00629   if(GetWindowRect(hwnd, &wrect))
00630     {
00631       rect.x1 = wrect.left;
00632       rect.y1 = wrect.top;
00633       rect.x2 = wrect.right;
00634       rect.y2 = wrect.bottom;
00635     }
00636   
00637   return true;
