00652 int XWINDOWSPROCESSMANAGER::GetWindowTitleHeight(HWND hwnd) 
00653 {
00654   RECT window_rectangle; 
00655   RECT client_rectangle;
00656   int  height;
00657   int  width;
00658     
00659   GetWindowRect(hwnd, &window_rectangle);
00660   GetClientRect(hwnd, &client_rectangle);
00661 
00662   height = (window_rectangle.bottom - window_rectangle.top) - (client_rectangle.bottom - client_rectangle.top);
00663   width  = (window_rectangle.right - window_rectangle.left) - (client_rectangle.right - client_rectangle.left);
00664     
00665   return height - (width/2);
00666 }
