00703 bool XWINDOWSPROCESSMANAGER::GetChildProcesses(XVECTOR<XPROCESS*>& applist) 
00704 {
00705   HANDLE threadSnapshot = CreateToolhelp32Snapshot(TH32CS_SNAPTHREAD, 0);
00706   if(threadSnapshot == INVALID_HANDLE_VALUE) 
00707     {
00708       return false;
00709     }
00710 
00711   THREADENTRY32 threadEntry;
00712   threadEntry.dwSize = sizeof(THREADENTRY32);
00713 
00714   if(!Thread32First(threadSnapshot, &threadEntry)) 
00715     {
00716       CloseHandle(threadSnapshot);
00717       return false;
00718     }
00719 
00720   do { XPROCESS* mainprocess = Application_GetProcessByID(threadEntry.th32OwnerProcessID, applist);
00721        if(mainprocess) 
00722          {
00723            if(mainprocess->GetProcessIDs()->IsEmpty())
00724              {
00725                mainprocess->GetProcessIDs()->Add(threadEntry.th32OwnerProcessID); 
00726              }
00727 
00728            mainprocess->GetProcessIDs()->Add(threadEntry.th32ThreadID);
00729          }
00730 
00731       } while (Thread32Next(threadSnapshot, &threadEntry));
00732 
00733   CloseHandle(threadSnapshot);
00734 
00735   return true;
00736 }
