00304                       Sleep(1000);
00305                     }
00306                    else break;
00307                 }
00308 
00309               if(svcstatus.dwCurrentState == SERVICE_STOPPED)
00310                 {
00311                   //wprintf(L"\n%s is stopped.\n", servicename);
