00314                 {
00315                   //wprintf(L"\n%s failed to stop.\n", servicename);
00316                 }
00317             }
00318 
00319 
00320           if(DeleteService(service))
00321             {
00322                status = true;
00323             }
00324         }
00325     }
00326 
00327 
00328   if(scmanager)
00329     {
00330       CloseServiceHandle(scmanager);
00331       scmanager = NULL;
00332     }
00333 
00334   if(service)
