00699 bool XWINDOWSSERVICEBASE::GetServiceConfig(XCHAR* servicename)
00700 {
00701   SC_HANDLE scmanager = OpenSCManager(0, 0, SC_MANAGER_ALL_ACCESS);
00702   if(scmanager == NULL) return false;
00703 
00704   SC_HANDLE service = OpenService(scmanager, servicename, SERVICE_ALL_ACCESS);
00705   if(service == NULL) return false;
00706 
00707   DWORD                   sizeneeded;
00708   DWORD                   buffersize;
00709   DWORD                   error; 
00710   bool                    status = false;
00711 
00712   if(service_config)  LocalFree(service_config);
00713 
00714   if(!QueryServiceConfig(service, NULL, 0, &sizeneeded))
00715     {
00716       error = GetLastError();
00717       if(error == ERROR_INSUFFICIENT_BUFFER)
00718         {
00719           buffersize = sizeneeded;
00720           service_config = (LPQUERY_SERVICE_CONFIG) LocalAlloc(LMEM_FIXED, buffersize);
00721 
00722           status = true;
00723         }       
00724     }
00725 
00726   if(status)
00727     {
00728       status = false;
00729 
00730       if(QueryServiceConfig(service, service_config, buffersize, &sizeneeded))      
00731         {        
00732           status = true;      
00733         }
00734     }
00735 
00736   CloseServiceHandle(scmanager);
00737 
00738   return status;
00739 }
