00964 void WINAPI XWINDOWSSERVICEBASE::ServiceMain(DWORD argc, LPWSTR* argv)
00965 {
00966   assert(service != NULL);
00967 
00968   service->statushandle = RegisterServiceCtrlHandlerEx(service->servicename, (LPHANDLER_FUNCTION_EX)ServiceCtrlHandler, (void*)XWINDOWSSERVICEBASE::service);
00969 
00970   if(service->statushandle == NULL)
00971     {
00972       throw GetLastError();
00973     }
00974 
00975   service->Start(argc, argv);
00976 }
