00990 void WINAPI XWINDOWSSERVICEBASE::ServiceCtrlHandler(DWORD control, DWORD eventtype, LPVOID eventdata, LPVOID context)
00991 {
00992   switch(control)
00993     {
00994       case SERVICE_CONTROL_STOP           : service->Stop();                     break;
00995       case SERVICE_CONTROL_PAUSE          : service->Pause();                    break;
00996       case SERVICE_CONTROL_CONTINUE       : service->Continue();                 break;
00997       case SERVICE_CONTROL_SHUTDOWN       : service->Shutdown();                 break;
00998       case SERVICE_CONTROL_POWEREVENT     : service->PowerEvent(eventtype);      break;
00999       case SERVICE_CONTROL_SESSIONCHANGE  : service->SessionChange(eventtype);   break;
01000       case SERVICE_CONTROL_INTERROGATE    : break;
01001                           default         : break;
01002     }
01003 }
