00188         void                                                                                QueryPerformanceData                                                            (PERF_DATA_BLOCK** perfdata, DWORD objectindex, DWORD counterindex)
00189                                                                                                 {
00190                                                                                                     //
00191                                                                                                     // Since i want to use the same allocated area for each query,
00192                                                                                                     // i declare CBUFFER as static.
00193                                                                                                     // The allocated is changed only when RegQueryValueEx return ERROR_MORE_DATA
00194                                                                                                     //
00195                                                                                                     static CBUFFER      buffer(XWINDOWSSYSTEM_TOTALBYTES);
00196                                                                                                     DWORD                           buffersize = buffer.GetSize();
00197                                                                                                     LONG                            result;
00198 
00199                                                                                                     XSTRING keyname;
00200                                                                                                     keyname.Format(__L("%d"), objectindex);
00201 
00202                                                                                                     buffer.Reset();
00203                                                                                                     while((result = RegQueryValueEx( HKEY_PERFORMANCE_DATA  ,   keyname.Get()
00204                                                                                                                                                                                                                     , NULL
00205                                                                                                                                                                                                                     , NULL
00206                                                                                                                                                                                                                     , buffer
00207                                                                                                                                                                                                                     , &buffersize )) == ERROR_MORE_DATA )
00208                                                                                                         {
00209                                                                                                             // Get a buffer that is big enough.
00210 
00211                                                                                                             buffersize += XWINDOWSSYSTEM_BYTEINCREMENT;
00212                                                                                                             buffer.Realloc(buffersize);
00213                                                                                                         }
00214 
00215                                                                                                     *perfdata = (PPERF_DATA_BLOCK) buffer.buffer;
00216                                                                                                 }
