00369 bool XWINDOWSVIRTUALDESKTOPMANAGER::IsCurrentDesktop(XCHAR* desktopname)
00370 {
00371   HDESK    hcurrentdesktop    = GetThreadDesktop(GetCurrentThreadId());
00372   XSTRING  currentdesktopname;
00373   DWORD    sizedesktopname    = 0;
00374 
00375   currentdesktopname.AdjustSize(_MAXSTR);
00376   GetUserObjectInformation(hcurrentdesktop, UOI_NAME, currentdesktopname.Get(), _MAXSTR - 1, &sizedesktopname);
00377   currentdesktopname.AdjustSize();
00378 
00379   return (!currentdesktopname.Compare(desktopname))?true:false;
