00510 bool XWINDOWSVIRTUALDESKTOPMANAGER::IsUserInteractive()
00511 {
00512   BOOL isuserinteractive = FALSE;
00513 
00514   HWINSTA hwinstation = GetProcessWindowStation();
00515   if(hwinstation != NULL)
00516     {
00517       USEROBJECTFLAGS uof = { 0 };
00518       if(GetUserObjectInformation(hwinstation, UOI_FLAGS, &uof, sizeof(USEROBJECTFLAGS), NULL) && ((uof.dwFlags & WSF_VISIBLE) == 0))
00519              isuserinteractive = FALSE;
00520         else isuserinteractive = TRUE;
00521     }
00522 
00523   return isuserinteractive?true:false;
00524 }
