00994                       break;
00995                   }
00996 
00997 
00998   
00999                 // Set up the query string with the adapter index we obtained above.
01000 
01001                 wchar_t dest[10];
01002 
01003                 memset((char*)dest, 0, 20);
01004                 _itow(index, dest, 10);
01005 
01006                 wstring objPath = L"Win32_NetWorkAdapterConfiguration.Index='" + wstring(dest);
