01030                 hr = pSvc->ExecMethod(bstrObjPath, SetIPConnectionMetricStr, 0, NULL, pInInst, &pOutInst, NULL);
01031                 if(hr != WBEM_S_NO_ERROR)
01032                   {
01033               XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("executing SetIPConnectionMetric. Error code [0x%08X]"), hr);                                                   
01034                       break;
