01046                       break;
01047                   }
01048 
01049                 long ret = V_I4(&ret_value);
01050                 if(ret != 0)
01051                   {
01052                       hr = E_FAIL;           
01053                   }
01054 
01055                 // Free up BSTR and VARIANT objects
01056 
01057                 VariantClear(&ret_value);
01058               VariantClear(&varArg1);
01059 
01060                 pObj->Release();    // Release objects not owned.            
01061             }
