01064       }
01065    else
01066       {
01067       XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("querying WMI for network adapters. Error code [0x%08X]"), hr);         
01068       }
01069 
01070   XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Done"));
01071 
01072   pSvc->Release();
01073   pLoc->Release();     
01074      
01075     return 0;   // Program successfully completed.
01076 }
